mapper.insert:
INSERT INTO BOARDIMG(IMG_NO
					 , ORIGIN_NAME
					 , CHANGE_NAME
					 , THUMBNAIL
					 , BOARD_NO)
				VALUES(BMG_SEQ.NEXTVAL
					 , #{originName}
					 , #{changeName}
					 , #{thumbnail}
					 , BNO_SEQ.CURRVAL)

mapper.update:
UPDATE BOARD
   SET STATUS = 'N'
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
   SET BOARD_TITLE = #{boardTitle}
     , BOARD_CONTENT = #{boardContent}
 WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT (*)
  FROM THUMB
 WHERE BOARD_NO = #{ boardNo }
   AND USER_NO = #{ userNo }

mapper.delete:
DELETE
  FROM THUMB
 WHERE BOARD_NO = #{ boardNo }
   AND USER_NO = #{ userNo }
